-- begin ITOGOVAYA_CAR_BASE
alter table ITOGOVAYA_CAR_BASE add constraint FK_ITOGOVAYA_CAR_BASE_CAR_MODEL_ID foreign key (CAR_MODEL_ID) references ITOGOVAYA_CAR_MODEL(ID)^
alter table ITOGOVAYA_CAR_BASE add constraint FK_ITOGOVAYA_CAR_BASE_CARD_ID foreign key (CARD_ID) references WF_CARD(ID)^
create index IDX_ITOGOVAYA_CAR_BASE_CAR_MODEL on ITOGOVAYA_CAR_BASE (CAR_MODEL_ID)^
-- end ITOGOVAYA_CAR_BASE
-- begin ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR
alter table ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR add constraint FK_ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR_CAR_ID foreign key (CAR_ID) references ITOGOVAYA_CAR_BASE(CARD_ID)^
alter table ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR add constraint FK_ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR_CAR_CONTRACTOR_ID foreign key (CAR_CONTRACTOR_ID) references DF_CONTRACTOR(CORRESPONDENT_ID)^
alter table ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR add constraint FK_ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR_BANK_ID foreign key (BANK_ID) references DF_BANK(ID)^
alter table ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR add constraint FK_ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR_CARD_ID foreign key (CARD_ID) references DF_DOC(CARD_ID)^
create index IDX_ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR_CAR_CONTRACTOR on ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR (CAR_CONTRACTOR_ID)^
create index IDX_ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR_CAR on ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR (CAR_ID)^
create index IDX_ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR_BANK on ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR (BANK_ID)^
-- end ITOGOVAYA_APPLICATION_FOR_PURCHASE_A_CAR
